import { ethers } from "ethers";
const PurchaseTokens = ({ state }) => {
  const purchaseTokens = async (event) => {
    event.preventDefault();
    const { contract } = state;
    const amountOfToken = document.querySelector("#amount").value;
    // const message = document.querySelector("#message").value;
    console.log(name, message, contract);
    const amount = { value: ethers.utils.parseEther("0.") };
    const transaction = await contract.purchaseTokens(
      amountOfToken,
      amount
    );
    await transaction.wait();
    console.log("Transaction is done");
  };
  return (
    <>
      <div className="container-md" style={{ width: "50%", marginTop: "25px" }}>
        <form onSubmit={purchaseTokens}>
          <div className="mb-3">
            <label className="form-label">Enter amount of tokens to purchase</label>
            <input
              type="text"
              className="form-control"
              id="amount"
              placeholder="Enter amount"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Message</label>
            <input
              type="text"
              className="form-control"
              id="message"
              placeholder="Enter Your Message"
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary"
            disabled={!state.contract}
          >
            Whitelist
          </button>
        </form>
      </div>
    </>
  );
};

export default PurchaseTokens;
