import abi from "./contracts/xalts.json";
import React, { useState, useEffect } from "react";
import PurchaseTokens from "./components/purchaseTokens.js";
import WhitelistBlacklist from "./components/WhitelistBlacklist.js";
import logo from "./logo-main.png";
import "./App.css";
// import { ethers } from "ethers";
const ethers = require("ethers");

function App() {
  const [state, setState] = useState({
    provider: null,
    signer: null,
    contract: null,
  });
  const [account, setAccount] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  const [balance, setBalance] = useState(null);
  // useEffect(() => {
  //   const connectWallet = async () => {
  //     const contractAddress = "0x46436dcb1b29b111a00bb61f5475b420ef1104eb";
  //     const contractABI = abi.abi;
  //     try {
  //       const { ethereum } = window;

  //       if (ethereum) {
  //         const account = await ethereum.request({
  //           method: "eth_requestAccounts",
  //         });

  //         window.ethereum.on("chainChanged", () => {
  //           window.location.reload();
  //         });

  //         window.ethereum.on("accountsChanged", () => {
  //           window.location.reload();
  //         });

  //         const provider = new ethers.providers.Web3Provider(ethereum);
  //         const signer = provider.getSigner();
  //         const contract = new ethers.Contract(
  //           contractAddress,
  //           contractABI,
  //           signer
  //         );
  //         setAccount(account);
  //         setState({ provider, signer, contract });
  //       } else {
  //         alert("Please install metamask");
  //       }
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   };
  //   connectWallet();
  // }, []);

  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", accountsChanged);
      window.ethereum.on("chainChanged", chainChanged);
    }
  }, []);

  const connectHandler = async () => {
    const contractAddress = "0x09dcc35e2360f672D4E08C38cfE1A13A18Cf73C8";
    const contractABI = abi.abi;
    const { ethereum } = window;
    if (window.ethereum) {
      try {
        const res = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        await accountsChanged(res[0]);
        window.ethereum.on("chainChanged", () => {
          window.location.reload();
        });

        window.ethereum.on("accountsChanged", () => {
          window.location.reload();
        });

        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(
          contractAddress,
          contractABI,
          signer
        );
        setAccount(account);
        setState({ provider, signer, contract });
      } catch (err) {
        console.error(err);
        setErrorMessage("There was a problem connecting to MetaMask");
      }
    } else {
      setErrorMessage("Install MetaMask");
    }
  };

  const accountsChanged = async (newAccount) => {
    setAccount(newAccount);
    try {
      const balance = await window.ethereum.request({
        method: "eth_getBalance",
        params: [newAccount.toString(), "latest"],
      });
      setBalance(ethers.utils.formatEther(balance));
    } catch (err) {
      console.error(err);
      setErrorMessage("There was a problem connecting to MetaMask");
    }
  };

  const chainChanged = () => {
    setErrorMessage(null);
    setAccount(null);
    setBalance(null);
  };
  return (
    <div style={{ backgroundColor: "#EFEFEF", height: "100%" }}>
      <button className="block-button" onClick={connectHandler}>
        Connect Wallet
      </button>
      <h3>Account: {account}</h3>
      <h3>Balance: {balance}</h3>
      <img src={logo} className="img-fluid" alt=".." width="10%" h />
      <p
        class="text-muted lead "
        style={{ marginTop: "10px", marginLeft: "5px" }}
      >
        <small>Connected Account - {account}</small>
      </p>
      <div className="container">
        <WhitelistBlacklist state={state} />
        <PurchaseTokens state={state} />
      </div>
    </div>
  );
}

export default App;
