import abi from "./contracts/xalts.json";
import React, { useState, useEffect } from "react";
import PurchaseTokens from "./components/purchaseTokens.js";
import WhitelistBlacklist from "./components/WhitelistBlacklist.js";
import logo from "./logo-main.png";
import "./App.css";
// import { ethers } from "ethers";
const ethers = require("ethers");

function App() {
  const [state, setState] = useState({
    provider: null,
    signer: null,
    contract: null,
  });
  const [account, setAccount] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  const [balance, setBalance] = useState(null);

  // useEffect(() => {
  //   if (window.ethereum) {
  //     window.ethereum.on("accountsChanged", accountsChanged);
  //     window.ethereum.on("chainChanged", chainChanged);
  //   }
  // }, []);

  const connectWallet = async () => {
    const contractAddress = "0x09dcc35e2360f672D4E08C38cfE1A13A18Cf73C8";
    const contractABI = abi.abi;
    const { ethereum } = window;
    if (window.ethereum) {
      try {
        const res = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        await accountsChanged(res[0]);

        // window.ethereum.on("chainChanged", () => {
        //   window.location.reload();
        // });

        // window.ethereum.on("accountsChanged", () => {
        //   window.location.reload();
        // });

        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(
          contractAddress,
          contractABI,
          signer
        );
        setAccount(account);
        setState({ provider, signer, contract });
      } catch (err) {
        console.error(err);
        setErrorMessage("There was a problem connecting to MetaMask");
      }
    } else {
      setErrorMessage("Install MetaMask");
    }
  };

  const accountsChanged = async (newAccount) => {
    setAccount(newAccount);
    try {
      const balance = await window.ethereum.request({
        method: "eth_getBalance",
        params: [newAccount.toString(), "latest"],
      });
      setBalance(ethers.utils.formatEther(balance));
    } catch (err) {
      console.error(err);
      setErrorMessage("There was a problem connecting to MetaMask");
    }
  };

  const chainChanged = () => {
    setErrorMessage(null);
    setAccount(null);
    setBalance(null);
  };
  return (
    <div
      style={{ backgroundColor: "#EFEFEF", height: "100%", marginTop: "0px" }}
    >
      <button
        style={{ marginLeft: "1280px", marginTop: "8px" }}
        className="block-button"
        onClick={connectWallet}
      >
        Connect Wallet
      </button>
      <h6 style={{ marginLeft: "980px", marginTop: "20px" }}>
        Account: {account}
      </h6>
      <h6 style={{ marginLeft: "980px", marginTop: "8px" }}>
        Balance: {balance} ETH
      </h6>
      <img
        src={logo}
        width="5%"
        height="5%"
        style={{ marginLeft: "10px", marginTop: "8px" }}
      />
      <h2
        style={{
          marginLeft: "406px",
          color: "Blue",
          marginTop: "-30px",
          fontSize: "35px",
        }}
      >
        XALTS Token [Whitelist/Blacklist]
      </h2>
      <div className="container">
        <WhitelistBlacklist state={state} />
        <PurchaseTokens state={state} />
      </div>
    </div>
  );
}

export default App;
