import { ethers } from "ethers";
const Blacklist = ({ state }) => {
  const blacklist = async (event) => {
    event.preventDefault();
    const { contract } = state;
    const addresses = document.querySelector("#addresses").value;
    // const message = document.querySelector("#message").value;
    console.log(addresses, contract);
    // const amount = { value: ethers.utils.parseEther("0.001") };
    const transaction = await contract.blacklist(addresses);
    await transaction.wait();
    console.log("Transaction is done");
  };
  return (
    <>
      <div className="container-md" style={{ width: "50%", marginTop: "25px" }}>
        <form onSubmit={blacklist}>
          <div className="mb-3">
            <label className="form-label">Addresses to blacklist</label>
            <input
              type="text"
              className="form-control"
              id="addresses"
              placeholder="Enter addresses"
            />
            <p style={{ color: "grey" }}>Example: ["0xDbBC6....", "0x7FEa6....", ...]</p>
          </div>
          <button
            type="submit"
            className="btn btn-primary"
            disabled={!state.contract}
          >
            Blacklist
          </button>
                <button
          type="submit"
          className="btn btn-primary"
          disabled={!state.contract}
        >
          Whitelist
        </button>
        </form>

      </div>
    </>
  );
};

export default Blacklist;
