import { colors } from "@mui/material";
import { ethers } from "ethers";
import React, { useState } from "react";
const WhitelistBlacklist = ({ state }) => {
  const [walletAddress, setwalletAddress] = useState(null);
  const [walletStatus, setwalletStatus] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  const [inputValue, setInputValue] = useState("");
  const [processedArray, setProcessedArray] = useState([]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const processInputToArray = () => {
    const newArray = inputValue.split(",").map((item) => item.trim());
    setProcessedArray(newArray);
  };
  const whitelist = async (event) => {
    event.preventDefault();
    const { contract } = state;
    const addresses = processedArray;
    console.log(addresses, contract);
    console.log(addresses[0]);
    console.log(addresses[1]);
    const transaction = await contract.whitelist(addresses);
    await transaction.wait();
    console.log("Transaction is done");
  };

  const blacklist = async (event) => {
    event.preventDefault();
    const { contract } = state;
    const addresses = document.querySelector("#addressesToBlacklist").value;
    console.log(addresses, contract);
    const transaction = await contract.blacklist(addresses);
    await transaction.wait();
    console.log("Transaction is done");
  };

  const getWalletAddresses = async (event) => {
    event.preventDefault();
    const { provider, signer, contract } = state;
    console.log("Signer: ", await signer.getAddress());
    const transaction = await contract.walletAddressList();
    let walletsArray = [];
    for (let i = 0; i < transaction[0].length; i++) {
      console.log(transaction[0][i]);
      walletsArray.push(transaction[0][i]);
    }
    setwalletAddress(walletsArray);
    console.log(walletAddress);
    console.log("Transaction is done", walletsArray);
  };

  const checkWalletStatus = async (event) => {
    event.preventDefault();
    const { provider, signer, contract } = state;
    const wallet = document.querySelector("#wallet").value;
    const isAddress = ethers.utils.isAddress(wallet);
    console.log("isValidAddress: ", isAddress);
    if (isAddress) {
      const transaction = await contract.isWhitelisted(wallet);
      setwalletStatus(transaction);
      setErrorMessage(false);
      console.log(transaction);
      console.log("Transaction is done", transaction);
    } else {
      console.log("Please enter a valid address");
      setwalletStatus(false);
      setErrorMessage(true);
    }
  };

  return (
    <>
      <div className="container-md" style={{ width: "50%", marginTop: "25px" }}>
        <form onSubmit={whitelist}>
          <div className="mb-3">
            <label className="form-label">Addresses to whitelist</label>
            {/* <input
              type="text"
              className="form-control"
              id="addressesToWhitelist"
              placeholder="Enter addresses"
            /> */}
            <input
              type="text"
              value={inputValue}
              onChange={handleInputChange}
            />
          </div>
          <p style={{ color: "grey" }}>
            Example: ["0xDbBC6....", "0x7FEa6....", ...]
          </p>
          <button
            onClick={processInputToArray}
            type="submit"
            className="btn btn-primary"
            disabled={!state.contract}
          >
            Whitelist
          </button>
        </form>
      </div>
      <div className="container-md" style={{ width: "50%", marginTop: "25px" }}>
        <form onSubmit={blacklist}>
          <div className="mb-3">
            <label className="form-label">Addresses to blacklist</label>
            <input
              type="text"
              className="form-control"
              id="addressesToBlacklist"
              placeholder="Enter addresses"
            />
            <p style={{ color: "grey" }}>
              Example: ["0xDbBC6....", "0x7FEa6....", ...]
            </p>
          </div>
          <button
            type="submit"
            className="btn btn-danger"
            disabled={!state.contract}
          >
            Blacklist
          </button>
        </form>

        <button
          className="block-button2"
          style={{ marginLeft: "0px", marginTop: "30px" }}
          onClick={getWalletAddresses}
        >
          Get All Wallets
        </button>
        <h5>{walletAddress}</h5>
        <form onSubmit={checkWalletStatus}>
          <div className="mb-3">
            {/* <label className="form-label">Check Status</label> */}
            <input
              type="text"
              className="form-control"
              id="wallet"
              placeholder="Enter address"
            />
          </div>
          <button
            type="submit"
            className="btn btn-secondary"
            disabled={!state.contract}
          >
            Check Wallet Status
          </button>
          <h5>
            Wallet Status:
            {walletStatus
              ? "Whitelisted"
              : errorMessage
              ? "Please enter an valid Ethereum address"
              : "Not Whitelisted"}
          </h5>
        </form>
      </div>
    </>
  );
};
export default WhitelistBlacklist;
