import abi from "./contract/chai.json";
import { Button, Paper, Stack, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import Buy from "./components/Buy";
// import { Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import Card from "react-bootstrap/Card";
import ReactDOM from "react-dom";
// import Memos from "./components/Memos";
import Whitelist from "./components/Whitelist";
import Blacklist from "./components/Blacklist";
import PurchaseToken from "./components/PurchaseToken";
// import connectWallet from "./components/connectWallet";
// import chai from "./chai.png";
import banner from "./banner.jpeg";
import "./App.css";

function App() {
  const [state, setState] = useState({
    provider: null,
    signer: null,
    contract: null,
  });
  const [account, setAccount] = useState("None");
  useEffect(() => {
  const connectWallet = async () => {
    const contractAddress = "0x46436dcb1b29b111a00bb61f5475b420ef1104eb";
    const contractABI = abi.abi;
    try {
      const { ethereum } = window;

      if (ethereum) {
        const account = await ethereum.request({
          method: "eth_requestAccounts",
        });

        window.ethereum.on("chainChanged", () => {
          window.location.reload();
        });

        window.ethereum.on("accountsChanged", () => {
          window.location.reload();
        });

        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(
          contractAddress,
          contractABI,
          signer
        );
        setAccount(account);
        setState({ provider, signer, contract });
      } else {
        alert("Please install metamask");
      }
    } catch (error) {
      console.log(error);
    }
  };
  connectWallet();
  }, []);
  // console.log(state);
  return (
    <div style={{ backgroundColor: "#EFEFEF", height: "100%" }}>
      {/* <div className="container" style={{ marginTop: "45px" }}>
        <connectWallet state={state} />
      </div> */}
      <img src={banner} className="img-fluid" alt=".." width="100%" />
      <p
        class="text-muted lead "
        style={{ marginTop: "10px", marginLeft: "5px" }}
      >
        <small>Connected Account - {account}</small>
      </p>
      <div className="container">
        <Whitelist state={state} />
      </div>
      <div className="container" style={{ marginTop: "45px" }}>
        <Blacklist state={state} />
      </div>
    </div>
  );
}

export default App;
