import abi from "./contract/xalts.json";
// import { Button, Paper, Stack, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import "bootstrap/dist/css/bootstrap.min.css";
// import Card from "react-bootstrap/Card";
// import ReactDOM from "react-dom";
import WhitelistBlacklist from "./components/WhitelistBlacklist";
// import PurchaseToken from "./components/PurchaseToken";
// import connectWallet from "./components/connectWallet";
import logo from "./logo-main.png";
// import banner from "./banner.jpeg";
import "./App.css";

function App() {
  const [state, setState] = useState({
    provider: null,
    signer: null,
    contract: null,
  });
  const [account, setAccount] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  const [balance, setBalance] = useState(null);
  // const [walletAddress, setwalletAddress] = useState(null);

  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on("accountChanged", accountChanged);
      window.ethereum.on("chainChanged", chainChanged);
    }
    // getWalletAddresses();
  }, []);

  const connectWallet = async () => {
    const contractAddress = "0x09dcc35e2360f672D4E08C38cfE1A13A18Cf73C8"; // XALTS Token Contract
    const contractABI = abi.abi;
    console.log("contractAddress", contractAddress);
    console.log("contractABI", contractABI);
    if (window.ethereum) {
      try {
        const res = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        await accountChanged(res[0]);

        window.ethereum.on("chainChanged", () => {
          window.location.reload();
        });

        window.ethereum.on("accountsChanged", () => {
          window.location.reload();
        });

        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        console.log(await signer.getAddress());
        const contract = new ethers.Contract(
          contractAddress,
          contractABI,
          signer
        );
        //       setAccount(account);
        setState({ provider, signer, contract });
      } catch (err) {
        console.error(err);
        setErrorMessage("There was a problem connecting to MetaMask");
      }
    } else {
      setErrorMessage("Install MetaMask");
    }

    // getWalletAddresses();
  };

    // const getWalletAddresses = async () => {
    //   // event.preventDefault();
    //   const { provider, signer, contract } = state;
    //   const transaction = await contract.walletAddressList();
    //   setwalletAddress(transaction[0]);
    //   console.log(walletAddress);
    //   console.log("Transaction is done", transaction[0]);
    // };

  const accountChanged = async (newAccount) => {
    setAccount(newAccount);
    try {
      const balance = await window.ethereum.request({
        method: "eth_getBalance",
        params: [newAccount.toString(), "latest"],
      });
      setBalance(ethers.utils.formatEther(balance));
    } catch (err) {
      console.error(err);
      setErrorMessage("There was a problem connecting to MetaMask");
    }
  };

  const chainChanged = () => {
    setErrorMessage(null);
    setAccount(null);
    setBalance(null);
  };

  return (
    <div style={{ backgroundColor: "#EFEFEF", height: "100%" }}>
      <button className="block-button" onClick={connectWallet}>
        Connect Wallet
      </button>
      <h5 style={{ marginLeft: "1020px" }}>Account: {account}</h5>
      <h5 style={{ marginLeft: "1020px" }}>Balance: {balance} ETH</h5>
      <img
        src={logo}
        width="5%"
        height="5%"
        style={{ marginLeft: "10px", marginTop: "10px " }}
      />
      {/* <img src={banner} className="img-fluid" alt=".." width="100%" /> */}
      <div className="container">
        <WhitelistBlacklist state={state} />
      </div>

    </div>
  );
}

export default App;
